import AppContext from './AppContext';

import { useState } from 'react';
import StayData from '../Data/Data.json';
import Description from '../components/Description/Description';
const AppState = (props) => {
  const [location, setLocation] = useState('Location');
  const [count, setCount] = useState(0);
  const [navExpand, setNavExpand] = useState(false);
  const [adult, setAdult] = useState(0);
  const [child, setChild] = useState(0);
  const [isOpen, setIsOpen] = useState(false);
  const [card, setCard] = useState(Description);

    let filterData = StayData.filter((item) => {
     
    return( item.city);

  
  });


  const Toggle = () => {
    setNavExpand(!navExpand);
   
  };



  //Filtro para cada ciudad (sin imprimir las tarjetas)
  const Filtering = (arr, filtro) => {
      let filterPlaces = arr.filter(item => item.city === filtro)
      return filterPlaces
  }
  StayData.forEach(card => {
    let filtrado = Filtering(StayData, card.city)
    
    Description(filtrado)
  })
  




  return (
    <AppContext.Provider
      value={{
        Toggle,
        Filtering,
        StayData,
        filterData,
        location,
        setLocation,
        count,
        setCount,
        navExpand,
        setNavExpand,
        adult,
        setAdult,
        child,
        setChild,
        isOpen,
        setIsOpen,
      }}>
      {props.children}
    </AppContext.Provider>
  );
};

export default AppState;
