import {
  Descriptions,
  SuperHost,
  TypeHost,
  Ratings,
  Title,
  Wrapper,
  Images,
  H1,
  HeadWrap,
} from './DescriptionElements';
import { AiFillStar } from 'react-icons/ai';
import StayData from '../../Data/Data.json';
import AppContext from '../../context/AppContext';
import { useContext } from 'react';

/* Esta imprimiendo las tarjetas */
const Description = () => {
   const {
    filtro
  } = useContext(AppContext);
  return (
    <>
      <div>
        <HeadWrap>
          <H1>Stays in Finland</H1>
          {filtro.city !== 0 && <div>{filtro.city}+ Stays</div>}
        </HeadWrap>
       
        <Descriptions>
          {filtro.map((data, index) => {
            return (
              <div key={index}>
                <Images src={data.photo} alt={data.type} />
                <Wrapper>
                  {data.superHost === true ? (
                    <SuperHost>SUPER HOST</SuperHost>
                  ) : null}

                  <TypeHost>
                    {data.type}. {data.beds} Beds
                  </TypeHost>

                  <Ratings>
                    <AiFillStar style={{ color: 'red' }} />
                    {data.rating}
                  </Ratings>
                </Wrapper>
                <Title>{data.title}</Title>
              </div>
            );
          })}
        </Descriptions>
      </div>
    </>
  );
};

export default Description;
